import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

/* Change Square to be a Function Component */
function Square(props) {

      return (
        <button className="square" onClick={() => props.onClick()}>

          {props.value }
        </button>
      );

  }

  class Board extends React.Component {

    /* Lifting State into Parent instead of Child */
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(100).fill(null),
        };
    }
    handleClick(i) {
        /* Create a copy of our squares array to make it immutable */ 
        const squares = this.state.squares.slice();

        /* If someone has won or if that square is already selected, do nothing when Square is clicked*/
    }
    renderSquare(i) {
      return <Square key={i} value={this.state.squares[i]}
      /* Pass in onClick function into Square */
      onClick={() => this.handleClick(i)}
      />;
    }
    callChildren(start, end) {
      for (let i = start; i < end; i++) {

        this.renderSquare(i);
      }
    }
    createGrid() {
      let grid = []
      let counter = 0;
      for (let i = 0; i < 10; i++) {

        grid.push(<div className="board-row">{this.callChildren(counter, counter + 10)}</div>)
        counter += 10;
      }
      return grid;
    }

    render() {

      return (
        <div>
          {this.createGrid()}
          {/* <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div> */}
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }


  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
